contracts/adapter/TestnetCelerBscFantomA.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.8.9;

import "../interfaces/AbstractMessageAdapter.sol";
import "sgn-v2-contracts/contracts/message/framework/MessageSenderApp.sol";
import "sgn-v2-contracts/contracts/message/framework/MessageReceiverApp.sol";

import "sgn-v2-contracts/contracts/message/interfaces/IMessageBus.sol";

contract TestnetCelerBscFantomAdapter is
    AbstractMessageAdapter,
    MessageSenderApp,
    MessageReceiverApp
{
    uint64 public constant BSC_CHAIN_ID = 97;
    uint64 public constant FANTOM_CHAIN_ID = 4002;
    address public remoteAdapterAddress;

    constructor(
        address _msgportAddress,
        address _messageBus
    ) AbstractMessageAdapter(_msgportAddress) {
        messageBus = _messageBus;
    }

    function setRemoteAdapterAddress(
        address _remoteAdapterAddress
    ) external onlyOwner {
        remoteAdapterAddress = _remoteAdapterAddress;
    }

    //////////////////////////////////////////
    // For sending
    //////////////////////////////////////////
    // override AbstractMessageAdapter
    function getRemoteAdapterAddress() public view override returns (address) {
        return remoteAdapterAddress;
    }

    // override AbstractMessageAdapter
    function getRelayFee(
        address _fromDappAddress,
        address _toDappAddress,
        bytes memory _messagePayload
    ) external view override returns (uint256) {
        return
            IMessageBus(messageBus).calcFee(
                abi.encode(_fromDappAddress, _toDappAddress, _messagePayload)
            );
    }

    // override AbstractMessageAdapter
    function getDeliveryGas(
        address _fromDappAddress,
        address _toDappAddress,
        bytes memory _messagePayload
    ) external view override returns (uint256) {
        return 0;
    }

    // override AbstractMessageAdapter
    function callRemoteAdapterRecv(
        address _remoteAdapterAddress,
        address _fromDappAddress,
        address _toDappAddress,
        bytes memory _messagePayload
    ) internal override returns (uint256) {
        bytes memory celerMessage = abi.encode(
            _fromDappAddress,
            _toDappAddress,
            _messagePayload
        );
        sendMessage(
            _remoteAdapterAddress,
            FANTOM_CHAIN_ID,
            celerMessage,
            msg.value
        );
        return 0;
    }

    //////////////////////////////////////////
    // For receiving
    //////////////////////////////////////////
    // override MessageApp
    // called by MessageBus on destination chain to receive cross-chain messages
    function executeMessage(
        address _srcContract,
        uint64 _srcChainId,
        bytes calldata _celerMessage,
        address // executor
    ) external payable override onlyMessageBus returns (ExecutionStatus) {
        require(_srcChainId == BSC_CHAIN_ID, "Invalid chainId");
        (
            address fromDappAddress,
            address toDappAddress,
            bytes memory messagePayload
        ) = abi.decode((_celerMessage), (address, address, bytes));
        recv(fromDappAddress, toDappAddress, messagePayload);
        return ExecutionStatus.Success;
    }

    // override AbstractMessageAdapter
    function permitted(
        address _fromDappAddress,
        address _toDappAddress,
        bytes memory messagePayload
    ) internal view override returns (bool) {
        return true;
    }
}

contracts/interfaces/AbstractMessageAdap.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.8.9;

import "./IMsgport.sol";

// adapter knows hot to send message to remote adapter.
abstract contract AbstractMessageAdapter {
    IMsgport public immutable localMsgport;

    constructor(address _localMsgportAddress) {
        localMsgport = IMsgport(_localMsgportAddress);
    }

    ////////////////////////////////////////
    // Abstract functions
    ////////////////////////////////////////
    // For receiving
    function permitted(
        address _fromDappAddress,
        address _toDappAddress,
        bytes memory messagePayload
    ) internal virtual returns (bool);

    // For sending
    function callRemoteAdapterRecv(
        address _remoteAdapterAddress,
        address _fromDappAddress,
        address _toDappAddress,
        bytes memory messagePayload
    ) internal virtual returns (uint256);

    function getRemoteAdapterAddress() public virtual returns (address);

    function getRelayFee(
        address _fromDappAddress,
        address _toDappAddress,
        bytes memory _messagePayload
    ) external view virtual returns (uint256);

    function getDeliveryGas(
        address _fromDappAddress,
        address _toDappAddress,
        bytes memory _messagePayload
    ) external view virtual returns (uint256);

    ////////////////////////////////////////
    // Public functions
    ////////////////////////////////////////
    // called by local msgport
    function send(
        address _fromDappAddress,
        address _toDappAddress,
        bytes calldata _messagePayload
    ) public payable returns (uint256) {
        // check this is called by local msgport
        require(
            msg.sender == address(localMsgport),
            "not allowed to be called by others except local msgport"
        );
        address remoteAdapterAddress = getRemoteAdapterAddress();
        require(remoteAdapterAddress != address(0), "remote adapter not set");

        return
            callRemoteAdapterRecv(
                remoteAdapterAddress,
                _fromDappAddress,
                _toDappAddress,
                _messagePayload
            );
    }

    // called by remote adapter through low level messaging contract or self
    function recv(
        address _fromDappAddress,
        address _toDappAddress,
        bytes memory _messagePayload
    ) public {
        require(
            permitted(_fromDappAddress, _toDappAddress, _messagePayload),
            "!permitted"
        );

        // call local msgport to receive message
        localMsgport.recv(_fromDappAddress, _toDappAddress, _messagePayload);
    }
}

